Blockly.Blocks['showFPStext'] = {
  init: function() {
    this.appendValueInput("text")
        .setCheck("String")
        .appendField("Show before FPS");
    this.setPreviousStatement(true, null);
    this.setNextStatement(true, null);
    this.setColour(230);
 this.setTooltip("");
 this.setHelpUrl("");
  }
};

Blockly.Python['showFPStext'] = function(block) {
  var value_text = Blockly.Python.valueToCode(block, 'text', Blockly.Python.ORDER_ATOMIC);
  // TODO: Assemble Python into code variable.
  // Never forget the /n at end for line blocks
  var code = 'showFPSScreen('+value_text+')\n';
  // showFPSScreen is defined in python.py
  return code;
};
